                    {console.log("Cat Video")}
                    <div className="tips-video__thumbnail">
                      <YouTube
                        videoId={video}
                        className="tips-video__youtubePlayer"
                      />
                    </div>
                  
                    <h6 className="tips-video__title">{video.title}</h6>
                  </li>
                ))
              : ""}
          </ul> */}

